import "./card.scss";

const Card = ({ album }) => {
  const artistName = album?.data?.artists?.items[0]?.profile?.name;
  const albumYear = album?.data?.date?.year;
  return (
    <div className="card">
      <div className="img-ctn">
        <img src={album?.data?.coverArt?.sources[0]?.url} alt="der" />
      </div>
      <div className="txt-ctn">
        <h5 className="title">{album?.data?.name}</h5>
        <p className="name">{artistName}</p>
        <p className="year">{albumYear}</p>
      </div>
    </div>
  );
};

export default Card;
