import { createAsyncThunk, createSlice } from "@reduxjs/toolkit";

export const getMovies = createAsyncThunk("getPosts", async (_, thunkAPI) => {
  axios.get(" ").then((res) => thunkAPI.dispatch(getMoviesSucces(res.data)));
});

export const moviesSlice = createSlice({
  name: "movies",
  initialState: { moviesData: [] },
  reducers: {
    getMoviesSucces: (state, { payload }) => {
      state.moviesData = payload;
    },
  },
});

export default moviesSlice.reducer;
