import React, { useEffect, useState } from "react";
import axios from "axios";

function App() {
  const [movies, setMovies] = useState([]);

  useEffect(() => {
    const fetchData = async () => {
      const options = {
        method: "GET",
        url: "https://spotify23.p.rapidapi.com/search/",
        params: {
          q: "<REQUIRED>",
          type: "multi",
          offset: "0",
          limit: "10",
          numberOfTopResults: "5",
        },
        headers: {
          "X-RapidAPI-Key":
            "5ae4e8bff9msh9f77a7d5c857a47p15b97djsn36981d386453",
          "X-RapidAPI-Host": "spotify23.p.rapidapi.com",
        },
      };

      try {
        const response = await axios.request(options);
        setMovies(response.data);
      } catch (error) {
        console.error(error);
      }
    };

    fetchData();
  }, []); // Le tableau vide en tant que deuxième argument signifie que cette fonction ne sera exécutée qu'une seule fois, équivalent à componentDidMount

  return (
    <div>
      {/* Utilisez la variable d'état "movies" dans votre composant */}
      {movies.map((movie, index) => (
        <div key={index}>{movie.joke}</div>
      ))}
    </div>
  );
}

export default App;
