import { createAsyncThunk, createSlice } from "@reduxjs/toolkit";
import { STATUS } from "../utils/status";

const initialState = {
  moviesData: [],
  moviesStatus: STATUS.IDLE,
};

export const moviesSlice = createSlice({
  name: "movies",
  initialState,
  reducers: {},
  extraReducers: (builder) => {
    builder
      .addCase(fetchAsyncMovies.pending, (state, action) => {
        state.moviesStatus = STATUS.LONDING;
      })

      .addCase(fetchAsyncMovies.fulfilled, (state, action) => {
        state.moviesData = action.payload;
        state.moviesStatus = STATUS.SUCCEEDED;
      });
  },
});

export const fetchAsyncMovies = createAsyncThunk("movies/fetch", async () => {
  const url =
    "https://spotify23.p.rapidapi.com/search/?q=q&type=multi&offset=0&limit=10&numberOfTopResults=5";
  const options = {
    method: "GET",
    headers: {
      "X-RapidAPI-Key": "5ae4e8bff9msh9f77a7d5c857a47p15b97djsn36981d386453",
      "X-RapidAPI-Host": "spotify23.p.rapidapi.com",
    },
  };

  try {
    const response = await fetch(url, options);
    const data = await response.json();
    console.log(data);
  } catch (error) {
    console.error(error);
  }
});

export const { getMoviesSucces } = moviesSlice.actions;
export default moviesSlice.reducer;
