import "./card.scss";

const Card = ({ album, artist }) => {
  // les donnes de la sections albums
  const albumName = album?.data?.name;
  const artistName =
    album?.data?.artists?.items[0]?.profile?.name ||
    artist?.items?.profile?.name;
  const albumYear = album?.data?.date?.year;

  // les donnes de la sections artists

  return (
    <div className="card">
      <div className="img-ctn">
        <img src={album?.data?.coverArt?.sources[0]?.url} alt="der" />
      </div>
      <div className="txt-ctn">
        <h5 className="title">{albumName}</h5>
        <p className="name">{artistName}</p>
        <p className="year">{albumYear}</p>
      </div>
    </div>
  );
};

export default Card;
